import React from "react";
import "../../styles/intro.css";
// Assuming 'photo' is the path to your image, e.g., from '../../assets/my-pic.jpeg.PNG'
// For this example, I'll use a placeholder image URL.
// Replace this with your actual image import:
// import photo from "../../assets/my-pic.jpeg.PNG";

function Intro() {
  return (
    <div className="intro-section">
      <div className="intro-container">
        <div className="intro-details">
          <h1 className="title">Hi! I Am</h1>
          <h1 className="name">Vishal Voorugonda</h1>
          <p className="content">
            I am a frontend-developer, a recently graduated Civil Engineer from
            Jayamukhi Institute of Technological and Science. After my B.Tech, I
            joined Coding Ninjas and
          </p>
          <button className="hire">Hire me</button>
        </div>
        <div className="my-pic-container">
          {/* Yellow Blob - Updated Path */}
          <svg className="blob yellow" viewBox="0 0 200 200">
            <path
              fill="#F9B233"
              d="M59.9,-61.8C74.6,-47.9,82.4,-23.9,78.2,-2.7C74.1,18.5,58.1,37,40.1,50.1C22.1,63.2,2.1,70.8,-17.3,71.7C-36.7,72.6,-55.5,66.8,-66.6,52.9C-77.7,39.1,-81.1,17.2,-74.6,-2.8C-68.1,-22.8,-51.7,-40.8,-33.9,-54.6C-16.1,-68.4,2.3,-78,19.3,-74.1C36.3,-70.2,45.2,-62.1,59.9,-61.8Z"
              transform="translate(100 100)"
            />
          </svg>

          {/* Blue Blob */}
          <svg className="blob blue" viewBox="0 0 200 200">
            <path
              fill="#007BFF"
              d="M54.4,-54.1C69.7,-39.2,80.8,-19.6,79.1,-1.6C77.4,16.4,63,32.7,47.7,47.7C32.3,62.8,16.2,76.5,-0.2,76.6C-16.6,76.8,-33.1,63.5,-45.2,48.4C-57.3,33.3,-65,16.6,-64.6,0.1C-64.1,-16.3,-55.5,-32.6,-43.4,-47.4C-31.2,-62.2,-15.6,-75.6,1.2,-76.5C18,-77.3,36,-65.6,54.4,-54.1Z"
              transform="translate(100 100)"
            />
          </svg>
          {/* Use a placeholder image or your actual imported 'photo' */}
          <img
            src="https://placehold.co/300x400/FF5733/FFFFFF?text=Your+Image" // Placeholder image
            // src={photo} // Uncomment this and ensure 'photo' is correctly imported
            alt="my-img"
            className="profile-pic"
          />
        </div>
      </div>
    </div>
  );
}

export default Intro;
