import React, { useEffect, useRef } from 'react'
import "../../styles/service.css"
function Service() {
  const cardsRef = useRef([])

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach(entry => {
          if (entry.isIntersecting) {
            entry.target.classList.add("visible");
          }
        });
      },
      {
        threshold: 0.3
      }
    );

    cardsRef.current.forEach((card) => {
      if (card) observer.observe(card);
    });

    return () => {
      cardsRef.current.forEach((card) => {
        if (card) observer.unobserve(card);
      });
    };
  }, []);
  return (
    <div className='myservice-container'>
      <div className='service-details'>
      <h1>My Services &</h1>
      <h1 className='skill'>Skills</h1>
      <p>Iam Working in corporate since last 12 years</p>
      <p>I have worked on than 100+ projects</p>
      <button className='download-cv'>Download CV</button>
      </div>
      <div className='cards-container'>
         <div className='card card-1' ref={(el)}>
            <h1>üòé</h1>
            <p className='designation'>CEO</p>
            <p className='content'>Working as CEO of Coding Ninjas is one of the founding member of COidng Ninjas as well.</p>
         </div>
           <div className='card card-2'>
            <h1>üòÄ</h1>
            <p className='designation'>Software Developer</p>
            <p className='content'>Working as CEO of Coding Ninjas is one of the founding member of COidng Ninjas as well.</p>
         </div>
           <div className='card card-3'>
            
            <h1>üòç</h1>
            <p className='designation'>web Developer</p>
            <p className='content'>Working as CEO of Coding Ninjas is one of the founding member of COidng Ninjas as well.</p>
         </div>
      </div>
       
    </div>
  )
}

export default Service
