import React, { useRef } from "react";
import "../../styles/experience.css";

function Experience() {
     const whiteDivRef = useRef();

  useEffect(() => {
    const handleScroll = () => {
      const rotation = window.scrollY * 0.3; // Adjust speed as needed
      if (whiteDivRef.current) {
        whiteDivRef.current.style.transform = `rotate(${rotation}deg)`;
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);
  return (
    <div className="experience-container">
      <div className="experience-details">
        <h1 className="title">Works for All these</h1>
        <h1 className="name">Brands & Clients</h1>
        <div className="details">
          <span>I have worked in 10+ compaines in last 12 years</span>
          <span>
            I have worked in different domains like Software Developer and Web
            Developer
          </span>
          <span>I have worked in 100+ projects in different domains</span>
          <span>I have sufficient experience to join any company as CEO</span>
        </div>
        <button className="hire">Hire me</button>
      </div>
      <div className="right-side">
        <div className="blue-circle"></div>
        <div className="yellow-circle"></div>
        <div className="rotating-wrapper">
          <div className="white-div">
            <div className="circle-div up">up</div>
            <div className="circle-div shopify">shopify</div>
            <div className="circle-div amazon">amazon</div>
            <div className="circle-div facebook">facebook</div>
            <div className="circle-div fi">fi</div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Experience;
