import React, { useEffect, useState } from 'react'
import "../../styles/toggle.css"
function Toggle() {
    const [toggle,setToggle] = useState(()=>{
          const saved =  localStorage.getItem("themeToggle")
           return saved === "true"
    })

    const handleToggle = () =>{
         setToggle((prev)=>!prev)

      localStorage.setItem(JSON.stringify("themetoggle",toggle))
    }

    useEffect(()=>{
       document.body.style.backgroundColor = toggle ? "#333":"#fff"
       document.body.style.color = toggle ? "#fff":"black"
    },[toggle])
  return (
    <div className='toggle-container' onClick={handleToggle}>
       <div className={`inner-circle ${toggle ? "active":""}`}></div>
    </div>
  )
}

export default Toggle
